<?php

class AttachmentController extends \BaseController {

  protected $attachment;   
  protected $uploads_path;
  
  /**
  * Inject the models.
  * @param Attachment $attachment
  */
  public function __construct(Attachment $attachment)
  {
    $this->attachment = $attachment;
    $this->uploads_path = public_path() . DIRECTORY_SEPARATOR . 'uploads' . DIRECTORY_SEPARATOR;
  }

  /**
	 * Get attachments for note.
	 *
	 * @param  int  $note_id   
	 * @return Response
	 */
	public function get_attachments($note_id)
	{
    $attachments = $this->attachment->get_attachments($note_id);

    if( Request::ajax() )
    {
      return Response::json($attachments);
    }
	}

